require ("core.min.js")
var lib = require ("core.min.js/src")
var $$$ = require ("vue.min.js/library/package.vue")
const {define, echo} = lib
const {zero, one} = lib

define ($$$.vue).class ("path", class {
	constructor (vue) {
		this.vue = vue
		this.data = {}
		}
	start () {
		var path = this.vue.app.package.get ("path")
		for (var i in path) this.set (i, path [i])
		return this
		}
	set (key, value) {
		return this.data [key] = value
		}
	get (key) {
		if (key) return this.data [key]
		else return this.data
		}
	})
