<script>
import vue, {lib, library} from "vue.min.js"
export default vue.next ({
	element: "date:time",
	attribute: ["format", "stamp", "offset"],
	setup: function (prop) {
		var format = vue.reference (prop.format)
		var stamp = vue.reference (lib.to.number (prop.stamp))
		var offset = vue.reference (prop.offset)
		return {format, stamp, offset}
		},
	method: {
		convert (... input) {
			return vue.date.format (... input)
			}
		},
	})
</script>

<template id="date:time">
	<string element="date:time">{{ convert (format, stamp, offset) }}</string>
</template>
