<script>
import vue, {lib, library} from "vue.min.js"
export default vue.next ({
	element: "form:input",
	attribute: ["for", "model", "complete", "capitalize", "correct", "spell"],
	setup: function (prop) {
		var label = vue.reference (prop.for)
		var model = vue.reference (vue.model.format (prop.model))
		var property = vue.reactive (vue.form.attribute (prop))
		if (vue.ini ("watch:legacy")) vue.watch (prop, function () {
			label.value = prop.for
			model.value = vue.model.format (prop.model)
			vue.react (property, vue.form.attribute (prop))
			})
		return {vue, label, model, property}
		},
	})
</script>

<template id="form:input">
	<input v-if="label" v-bind:for="label" v-model="vue.model.data [model]" v-on:focus="vue.form.focus" v-on:blur="vue.form.blur" v-bind:autocomplete="property.complete" v-bind:autocapitalize="property.capitalize" v-bind:autocorrect="property.correct" v-bind:spellcheck="property.spell" element="form:input">
	<input v-model="vue.model.data [model]" v-bind:autocomplete="property.complete" v-bind:autocapitalize="property.capitalize" v-bind:autocorrect="property.correct" v-bind:spellcheck="property.spell" element="form:input" v-else>
</template>
