<script>
import vue, {lib, library} from "vue.min.js"
export default vue.next ({
	element: "form:bullet",
	attribute: ["for", "model"],
	setup: function (prop) {
		var label = vue.reference (prop.for)
		var model = vue.reference (vue.model.format (prop.model))
		if (vue.ini ("watch:legacy")) vue.watch (prop, function () {
			label.value = prop.for
			model.value = vue.model.format (prop.model)
			})
		return {vue, label, model}
		},
	})
</script>

<template id="form:bullet">
	<input v-if="label" v-bind:for="label" v-model="vue.model.data [model]" v-on:focus="vue.form.focus" v-on:blur="vue.form.blur" type="radio" element="form:bullet">
	<input v-model="vue.model.data [model]" type="radio" element="form:bullet" v-else>
</template>
