<script>
import vue, {lib, library} from "vue.min.js"
export default vue.next ({
	element: "form:number",
	attribute: ["for", "model", "complete", "capitalize", "correct", "spell", "value"],
	setup: function (prop) {
		var label = vue.reference (prop.for)
		var model = vue.reference (vue.model.format (prop.model))
		var attribute = vue.reactive (vue.form.attribute (prop))
		if (lib.is.define (prop.value)) if (prop.value) vue.model (prop.model, lib.number (prop.value))
		else vue.model (prop.model, "")
		if (vue.ini ("watch:legacy")) vue.watch (prop, function () {
			label.value = prop.for
			model.value = vue.model.format (prop.model)
			vue.react (attribute, vue.form.attribute (prop))
			})
		return {vue, label, model, attribute}
		},
	})
</script>

<template id="form:number">
	<input v-if="label" v-bind:for="label" v-model="vue.model.data [model]" v-on:focus="vue.form.focus" v-on:blur="vue.form.blur" v-bind:autocomplete="attribute.complete" v-bind:autocapitalize="attribute.capitalize" v-bind:autocorrect="attribute.correct" v-bind:spellcheck="attribute.spell" type="number" element="form:number">
	<input v-model="vue.model.data [model]" v-bind:autocomplete="attribute.complete" v-bind:autocapitalize="attribute.capitalize" v-bind:autocorrect="attribute.correct" v-bind:spellcheck="attribute.spell" type="number" element="form:number" v-else>
</template>
