<script>
import vue, {lib, library} from "vue.min.js"
export default vue.next ({
	element: "icon:symbol",
	attribute: ["src", "type", "weight"],
	setup: function (prop) {
		var src = vue.reference (prop.src)
		var weight = vue.reference (style [prop.weight] || prop.weight || style ["bold-medium"])
		var type = vue.reference ("material-symbol")
		if (prop.type === "line") type.value = "material-symbol-line"
		else type.value = "material-symbol"
		type.value = type.value.implode (weight.value, ("weight").concat ("__")).value ()
		return {src, type}
		},
	})
var style = {"light-ultra": 100, "light-medium": 200, "light": 300, "regular": 400, "bold-light": 500, "bold-medium": 600, "bold": 700}
</script>

<template id="icon:symbol">
	<span v-if="src" v-bind:class="type" element="icon:symbol" icon>{{ src }}</span>
	<span v-bind:class="type" element="icon:symbol" icon v-else><slot slot/></span>
</template>
