<script>
import vue, {lib, library} from "vue.min.js"
export default vue.next ({
	element: "img:logo",
	attribute: ["src", "slot"],
	setup: function (prop) {
		var src = vue.reference (prop.src)
		if (prop.slot === "public") src.value = vue.static.logo.path (src.value)
		else src.value = vue.theme.logo.path (src.value)
		return {vue, src}
		},
	})
</script>

<template id="img:logo" type="element">
	<img v-bind:src="src"/>
</template>
