<script>
import vue, {lib, library} from "vue.min.js"
export default vue.next ({id: "vue:slot", attribute: ["slot", "layout", "component", "element", "image"]})
</script>

<template id="vue:slot">
	<img:sys v-if="image" v-bind:slot="image"><slot slot/></img:sys>
	<dom v-else-if="layout" v-bind:slot="layout"><slot slot/></dom>
	<dom v-else-if="component" v-bind:slot="component"><slot slot/></dom>
	<dom v-else-if="element" v-bind:slot="element"><slot slot/></dom>
	<dom v-bind:slot="slot" v-else><slot slot/></dom>
</template>
