import vue, {lib, library} from "vue.min.js"
import dom from "vue.min.js/plugin/dom"
import plugin from "vue.min.js/plugin/extension"
let {define} = lib
let {zero, one} = lib

export default {
	install: function (app, option) {
		app.directive ("component", function (element, attribute) {})
		app.directive ("element", function (element, attribute) {})
		if (null) vue.directive (app, "auto-complete", function (element, attribute) { lib.dom (element).attribute (("auto" + "complete"), lib.to_bool (attribute.value)) })
		if (null) vue.directive (app, "auto-capitalize", function (element, attribute) { lib.dom (element).attribute (("auto" + "capitalize"), lib.to_bool (attribute.value)) })
		if (null) vue.directive (app, "auto-correct", function (element, attribute) { lib.dom (element).attribute (("auto" + "correct"), lib.to_bool (attribute.value)) })
		if (null) vue.directive (app, "spell-check", function (element, attribute) { lib.dom (element).attribute (("spell" + "check"), lib.to_boolean (attribute.value)) })
		for (var i in plugin) app.directive (i, plugin [i])
		for (var i in dom) app.component (i, dom [i])
		}
	}
