import vue, {lib, library} from "vue.min.js"
import ini from "@/package.json"
let {define} = lib
let {zero, one} = lib.number

window.url = lib.url.parse (lib.string (window.location))
document.url = vue.reference ()

vue.window = function () {}
vue.window.on = function (key, value) { vue.document.on [key].push (value) }

vue.document = function () {}
vue.document.on = function (key, value) { vue.document.on [key].push (value) }
vue.document.on.click = []

vue.document.emit = function (key) {
	if (key === "click") {
		lib.event.listener (document, "click", function (element) {
			for (var i in vue.document.on.click) vue.document.on.click [i].call (this, element)
			})
		}
	if (key === "cursor:in") {}
	if (key === "cursor:out") {}
	}
