import vue, {lib, library} from "vue.min.js"
import ini from "@/package.json"
let {define} = lib
let {zero, one} = lib

if (null) {
	vue.db = new lib.db
	vue.db.start ({slot: "socket", socket: vue.socket})
	vue.db.setup = function (context) {
		if (null) {
			if (vue.package.db) {
				if ((vue.package.db.setup === true) === false) {
					for (var i in vue.package.db.collection) {
						vue.db.collection.create (i).set (vue.package.db.collection [i]).then (function (db) {
							if (context) context.call (db)
							})
						}
					}
				}
			}
		}
	}
