import vue, {lib, library} from "vue.min.js"
import ini from "@/package.json"
let {define} = lib
let {zero, one} = lib

vue.form = function () {}

vue.form.focus = function (element) {
	var label = lib.dom (element.target).attribute ("for").split (lib.string.char.space)
	for (var i in label) lib.dom (lib.dom.query (label [i])).class.insert ("--focus")
	}

vue.form.blur = function (element) {
	var label = lib.dom (element.target).attribute ("for").split (lib.string.char.space)
	for (var i in label) lib.dom (lib.dom.query (label [i])).class.delete ("--focus")
	}

vue.form.attribute = function (attribute) {
	return {
		complete: lib.is.define (attribute.complete) ? lib.to_bool (attribute.complete) : null,
		capitalize: lib.is.define (attribute.capitalize) ? lib.to_bool (attribute.capitalize, "switch") : null,
		correct: lib.is.define (attribute.correct) ? lib.to_bool (attribute.correct) : null,
		spell: lib.is.define (attribute.spell) ? lib.to_boolean (attribute.spell) : null,
		}
	}
