import vue, {the, lib, library} from "vue.min.js"
import ini from "@/package.json"
let {define} = lib
let {zero, one} = lib.number

the.ref = {}
the.reference = function (key) { return the.ref [key] }
the.var = function (key, value) {
	if (key) {
		if (arguments.length > one) return the.ref [key] = vue.reference (value)
		return the.ref [key].value
		}
	return the.ref
	}

the.react = vue.reactive ()
the.reactive = function (key, value) {
	if (lib.is.object (key)) vue.react (the.react, key)
	else if (key) if (arguments.length === one) return the.react [key]
	else return the.react [key] = value
	else return the.react
	}

the.snapshot = function (context, auto = true) {
	if (auto) context.call ()
	return lib.time.interval (context, (the.snapshot.interval || one))
	}

the.snapshot.interval = 10
