import vue, {lib, library} from "vue.min.js"
import ini from "@/package.json"
let {define} = lib
let {zero, one} = lib

vue.media = function () {}

vue.media.query = function () {}

vue.media.query.screen = function () {}

vue.media.query.screen.splash = function () {
	var show = vue.ini ("media-query:screen-splash")
	var timeout = vue.ini ("media-query:screen-splash/timeout")
	if (vue.url.path === "/") if (lib.date.expire (vue.time.stamp.value, vue.session.get ("media-query:screen-splash"), {second: timeout})) lib.time.sleep (function () { vue.media.query.screen.splash.touch () }, show)
	else vue.media.query.screen.splash.touch ()
	else vue.media.query.screen.splash.touch ()
	}

vue.media.query.screen.splash.touch = function () {
	vue.media.query.screen.splash.show.status.value = null
	vue.session.set ({"media-query:screen-splash": vue.time.stamp.value})
	}

vue.media.query.screen.splash.show = function (status) {
	if (status) vue.media.query.screen.splash.show.status.value = true
	return vue.media.query.screen.splash.show.status.value
	}

vue.media.query.screen.splash.start = function () {
	vue.media.query.screen.splash.show (true)
	vue.media.query.screen.splash ()
	}

vue.media.query.screen.splash.show.status = vue.reference ()
